if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/development/Nodejs/OpenSource/cleany/cra-react-play/before_push/node_modules/.pnpm/mathjs@11.8.0/node_modules/mathjs/bin/node_modules:/mnt/d/development/Nodejs/OpenSource/cleany/cra-react-play/before_push/node_modules/.pnpm/mathjs@11.8.0/node_modules/mathjs/node_modules:/mnt/d/development/Nodejs/OpenSource/cleany/cra-react-play/before_push/node_modules/.pnpm/mathjs@11.8.0/node_modules:/mnt/d/development/Nodejs/OpenSource/cleany/cra-react-play/before_push/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../node_modules/.pnpm/mathjs@11.8.0/node_modules/mathjs/bin/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../node_modules/.pnpm/mathjs@11.8.0/node_modules/mathjs/bin/cli.js" $args
  }
  $ret=$LASTEXITCODE
